import React, { useState } from 'react';
import questionsData from '/Users/shivakhanduri/quiz-app/src/questions_output.json';  // Correct path to JSON file

const App = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [showCorrectAnswer, setShowCorrectAnswer] = useState(false);
  const [score, setScore] = useState(0);
  const [isQuizOver, setIsQuizOver] = useState(false);

  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
    setShowCorrectAnswer(true);

    if (answer === questionsData[currentQuestion].correctAnswer) {
      setScore(score + 1);
    }
  };

  const handleNextQuestion = () => {
    setShowCorrectAnswer(false);
    setSelectedAnswer('');

    if (currentQuestion < questionsData.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setIsQuizOver(true);
    }
  };

  return (
    <div className="app-container">
      <h1>Quiz Application</h1>
      {!isQuizOver && (
        <div className="quiz-question">
          <h2>{questionsData[currentQuestion].question}</h2>
          <ul>
            {questionsData[currentQuestion].options.map((option, index) => (
              <li
                key={index}
                onClick={() => handleAnswerSelect(option)}
                className={`quiz-option ${selectedAnswer === option ? 'selected' : ''}`}
              >
                {option}
              </li>
            ))}
          </ul>

          {showCorrectAnswer && (
            <div className="feedback">
              {selectedAnswer === questionsData[currentQuestion].correctAnswer ? (
                <p className="correct">Correct!</p>
              ) : (
                <p className="incorrect">
                  Incorrect! The correct answer is: {questionsData[currentQuestion].correctAnswer}
                </p>
              )}
              <button onClick={handleNextQuestion}>Next Question</button>
            </div>
          )}
        </div>
      )}

      {isQuizOver && (
        <div>
          <h2>Quiz Over!</h2>
          <p>Your score: {score}/{questionsData.length}</p>
        </div>
      )}
    </div>
  );
};

export default App;
